import React from "react";

import Layout from "../components/Layout";

const Home = () => {
  function changeText(e) {
    let text = e.target.textContent;
    let button = document.getElementById("btn");
    let dropdown = document.getElementById("content");
    button.innerText = text;
  }

  return (
    <React.Fragment>
      <Layout>
        <main>
          <h1>Find a beach cleanup near you!</h1>
          <div className="dropdown">
            <button className="dropdown-btn" id="btn">
              Select your country
            </button>
            <div className="dropdown-content" id="content">
              <p onClick={changeText}>Hong Kong</p>
              <p onClick={changeText}>United States</p>
              <p onClick={changeText}>Canada</p>
            </div>
          </div>
        </main>
      </Layout>

      <style jsx>{`
        main {
          height: calc(100vh - 55px);
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        // Dropdown

        .dropdown {
          position: relative;
          display: inline-block;
        }

        .dropdown-btn {
          min-width: 160px;
        }

        .dropdown-content {
          display: none;
          position: absolute;
          background: #f9f9f9;
          min-width: 160px;
          box-shadow: 0 8px 16px 0 rgba(0, 0, 0, 0.2);
          padding: 12px 16px;
          z-index: 1;
        }

        .dropdown:hover .dropdown-content {
          display: block;
        }

        p {
          cursor: pointer;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Home;
