import React, { useContext, useState, useEffect } from "react";
import { useRouter } from "next/router";

import Layout from "../../components/Layout";
import { CountryContext } from "../../context/CountryContext";

const Results = () => {
  const router = useRouter();
  const country = useContext(CountryContext);
  const [state, setState] = useState();

  fetch("http://localhost:1337/hostings")
    .then((res) => res.json())
    .then((data) =>
      setState(
        data.filter(
          (hosting) =>
            hosting.country.toLowerCase() === country.country.toLowerCase()
        )
      )
    )
    .catch((err) => console.log(err));

  useEffect(() => {
    if (state !== undefined) {
      if (state.length === 0) {
        alert(`No beach cleanups found for ${country.country}`);
        router.push("/");
      }
    }
  }, [state]);

  return (
    <React.Fragment>
      <Layout>
        {state === undefined ? (
          <div>Loading...</div>
        ) : (
          state.map((hosting) => (
            <div className="container">
              <p>Location: {hosting.location}</p>
              <p>Date: {`${hosting.day}th ${hosting.month} ${hosting.year}`}</p>
              {/*<p>Description: {hosting.description}</p>*/}
              {/*<p>Remarks: {hosting.remarks}</p>*/}
              {/*<p>Country: {hosting.country}</p>*/}
            </div>
          ))
        )}
      </Layout>

      <style jsx>{`
        .container {
          display: flex;
          flex-direction: column;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Results;
