import React, { useContext, useState } from "react";

import Layout from "../../components/Layout";
import { CountryContext } from "../../context/CountryContext";

const Results = () => {
  const country = useContext(CountryContext);
  const [state, setState] = useState([]);

  fetch("http://localhost:1337/hostings")
    .then((res) => res.json())
    .then((data) =>
      setState(
        data.filter(
          (hosting) =>
            hosting.country.toLowerCase() === country.country.toLowerCase()
        )
      )
    )
    .catch((err) => console.log(err));

  return (
    <React.Fragment>
      <Layout>
        {state === undefined ? (
          <div>Loading...</div>
        ) : (
          state.map((hosting) => (
            <div>
              <p>Location: {hosting.location}</p>
              <p>Date: {`${hosting.day}`}</p>
              <p>Description: {hosting.description}</p>
              <p>Remarks: {hosting.remarks}</p>
              <p>Country: {hosting.country}</p>
            </div>
          ))
        )}
      </Layout>
    </React.Fragment>
  );
};

export default Results;
