import React from "react";

import Layout from "../components/Layout";

const About = () => {
  return (
    <React.Fragment>
      <Layout>
        <p>
          We are a team of 3 that all share a love for the ocean and it pains us
          to know that marine animals are suffering due to human interference.
          According to National Geographic, there are 5.25 trillion pieces of
          plastic debris floating around in the ocean. Therefore, in an effort
          to make a difference, to do our part in protecting the environment, we
          have created a platform for people to easily host and/or join beach
          cleanups in their region.
        </p>
      </Layout>

      <style jsx>{`
        p {
          margin: 0 auto;
          width: 40%;
        }
      `}</style>
    </React.Fragment>
  );
};

export default About;
