import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";

import Layout from "../../../components/Layout";

const HostingSpecifics = () => {
  const router = useRouter();
  const { id } = router.query;
  const [state, setState] = useState();

  fetch("http://localhost:1337/hostings")
    .then((res) => res.json())
    .then((data) => {
      setState(data.filter((hosting) => parseInt(hosting.id) === parseInt(id)));
    })
    .catch((err) => console.log(err));

  return (
    <React.Fragment>
      <Layout>
        {state === undefined ? (
          <div>Loading...</div>
        ) : (
          <div>
            <p>Location: {state[0].location}</p>
            <p>
              Date: {`${state[0].day}th ${state[0].month} ${state[0].year}`}
            </p>
            <p>Description: {state[0].description}</p>
            <p>Remarks: {state[0].remarks}</p>
          </div>
        )}
      </Layout>
    </React.Fragment>
  );
};

export default HostingSpecifics;
