import React, { useState, useContext, useEffect } from "react";
import Link from "next/link";

import Layout from "../components/Layout";
import { UserContext } from "../context/UserContext";
import { determineEnd, determineMonth } from "../Utils";

const SubscribedEvents = () => {
  const userData = useContext(UserContext);
  const [state, setState] = useState();

  useEffect(() => {
    if (userData !== undefined) {
      setState(userData.user.hostings);
      console.log(state);
    }
  }, []);

  return (
    <React.Fragment>
      <Layout>
        <main>
          {state === undefined ? (
            <div>Loading...</div>
          ) : (
            state.map((hosting) => (
              <div className="container">
                <Link href={`/results/${hosting.uuid}`}>
                  <p className="location">Location: {hosting.location}</p>
                </Link>
                <p className="date">
                  Date:{" "}
                  {`${hosting.date.slice(8, 10)}${determineEnd(
                    hosting.date.charAt(9)
                  )} ${determineMonth(
                    hosting.date.slice(5, 7)
                  )} ${hosting.date.slice(0, 4)}`}
                </p>
              </div>
            ))
          )}
        </main>
      </Layout>
    </React.Fragment>
  );
};

export default SubscribedEvents;
