import React, { useEffect } from "react";
import { useRouter } from "next/router";

import Layout from "../../components/Layout";

const CreateForum = () => {
  const router = useRouter();

  useEffect(() => {
    if (
      localStorage.getItem("identifier") === null ||
      localStorage.getItem("password") === null
    ) {
      router.push("/login");
    }
  }, []);

  return (
    <React.Fragment>
      <Layout>
        <main>
          <h1>Create Post</h1>
          <form>
            <input
              required
              type="text"
              placeholder="Title"
              id="title"
              name="title"
            />
            <textarea
              required
              name="body"
              id="body"
              cols="64"
              rows="30"
              placeholder="Post Body"
            />
            <button type={"submit"}>Post</button>
          </form>
        </main>
      </Layout>

      <style jsx>{`
        h1 {
          text-align: center;
          margin-top: 15px;
        }

        form {
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        input {
          display: block;
          height: 25px;
          width: 500px;
          margin-top: 30px;
        }

        textarea {
          margin-top: 30px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default CreateForum;
