import React from "react";

import Layout from "../../components/Layout";
import countries from "../../countries";
import Dropdown from "../../components/Dropdown";

const Host = () => {
  const initialText = "Select Country";

  return (
    <React.Fragment>
      <Layout>
        <main>
          <h1>Host your own Beach Cleanup</h1>
          <form>
            <label htmlFor="location">
              Location of your cleanup (Example: Stanley Beach, Hong Kong)
            </label>
            <input type="text" id="location" name="location" />
            <label htmlFor="country">Choose the country of this cleanup</label>
            <Dropdown array={countries} text={initialText} />
          </form>
        </main>
      </Layout>

      <style jsx>{`
        h1 {
          text-align: center;
          margin-top: 15px;
        }

        form {
          display: flex;
          flex-direction: column;
          align-items: center;
        }

        label {
          display: block;
          margin-top: 10px;
        }

        input {
          display: block;
          height: 25px;
        }
      `}</style>
    </React.Fragment>
  );
};

export default Host;
